import telebot
from logic_ai import get_class
from confic import TOKEN

bot = telebot.TeleBot(TOKEN)

@bot.message_handler(commands=['start'])
def send_welcome(message):
    markup = telebot.types.InlineKeyboardMarkup()
    markup.add(
        telebot.types.InlineKeyboardButton('üåç –°–≤—è–∑—å –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ –ø–æ—Ç–µ–ø–ª–µ–Ω–∏—è —Å –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫–æ–π –º—É—Å–æ—Ä–∞', callback_data='global_warming'),
        telebot.types.InlineKeyboardButton('‚ôªÔ∏è –ü–µ—Ä–µ—Ä–∞–±–æ—Ç–∫–∞ —Ä–∞–∑–Ω—ã—Ö —Ç–∏–ø–æ–≤ –º—É—Å–æ—Ä–∞', callback_data='recycling')
    )
    markup.add(telebot.types.InlineKeyboardButton('üì∑ –û–ø—Ä–µ–¥–µ–ª–∏—Ç—å —Ç–∏–ø –º—É—Å–æ—Ä–∞ –ø–æ —Ñ–æ—Ç–æ', callback_data='upload_photo'))
    
    bot.send_message(message.chat.id, '–ü—Ä–∏–≤–µ—Ç! –Ø —ç–∫–æ–±–æ—Ç! ‚ôªÔ∏è')
    bot.send_message(message.chat.id, '–ó–∞–≥—Ä—è–∑–Ω–µ–Ω–∏–µ –Ω–∞—à–µ–π –ø–ª–∞–Ω–µ—Ç—ã - –æ—á–µ–Ω—å –Ω–µ–¥–æ–æ—Ü–µ–Ω—ë–Ω–Ω–∞—è –ø—Ä–æ–±–ª–µ–º–∞. –û–Ω–∞ –ø–æ–º–∏–º–æ –Ω–µ—É–¥–æ–±—Å—Ç–≤–∞ –¥–ª—è –ª—é–¥–µ–π –≤—ã–∑—ã–≤–∞–µ—Ç –±–æ–ª—å—à–∏–µ —ç–∫–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–µ –ø—Ä–æ–±–ª–µ–º—ã, —Å–ø–æ—Å–æ–ø—Å—Ç–≤—É–µ—Ç –∏–∑–º–µ–Ω–µ–Ω–∏—é –∫–ª–∏–º–∞—Ç–∞, –∞ —Ç–∞–∫–∂–µ –≤—Ä–µ–¥–Ω–æ —ç–∫–æ—Å–∏—Å—Ç–µ–º–µ –ó–µ–º–ª–∏')
    bot.send_message(message.chat.id, '–≠—Ç–æ—Ç –±–æ—Ç –ø–æ—Å–≤—è—â—ë–Ω –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫–µ –º—É—Å–æ—Ä–∞. –í—ã–±–µ—Ä–∏—Ç–µ, –∫–∞–∫—É—é —Ñ—É–Ω–∫—Ü–∏—é –±–æ—Ç–∞ –≤—ã —Ö–æ—Ç–∏—Ç–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å.')
    bot.send_message(message.chat.id, '–ü–æ—Å–ª–µ –ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –Ω–∞—à–∏–º –±–æ—Ç–æ–º, –æ–ø–∏—à–∏—Ç–µ —Å–≤–æ–∏ –≤–ø–µ—á–∞—Ç–ª–µ–Ω–∏—è –≤ –ø–∏—Å—å–º–µ –Ω–∞–º –Ω–∞ –ø–æ—á—Ç—É: kumetsanton@gmail.com. –ú—ã –±—É–¥–µ–º –∂–¥–∞—Ç—å! ‚ò∫Ô∏è', reply_markup=markup)

@bot.callback_query_handler(func=lambda call: True)
def callback_handler(call):
    if call.data == 'global_warming':
        send_warming(call.message)
    elif call.data == 'recycling':
        send_recycling(call.message)
    elif call.data == 'upload_photo':
        bot.send_message(call.message.chat.id, '–ï—Å–ª–∏ —Ç—ã –Ω–µ –∑–Ω–∞–µ—à—å, –∫–∞–∫–æ–π —Ç–∏–ø –º—É—Å–æ—Ä–∞ –ø–µ—Ä–µ–¥ —Ç–æ–±–æ–π, –∑–∞–≥—Ä—É–∂–∞–π —Ñ–æ—Ç–æ, —á—Ç–æ–±—ã –ø–æ –Ω–µ–º—É –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å —Ç–∏–ø –ø–µ—Ä–µ—Ä–æ–±–∞—Ç—ã–≤–∞–µ–º–æ–≥–æ –º—É—Å–æ—Ä–∞. –ï—Å—Ç—å 4 –∫–ª–∞—Å—Å–∞: –º–∞–∫—É–ª–∞—Ç—É—Ä–∞, –±–∞—Ç–∞—Ä–µ–π–∫–∏, –ø–ª–∞—Å—Ç–∏–∫–æ–≤—ã–µ –±—É—Ç—ã–ª–∫–∏, —Å—Ç–µ–∫–ª–æ. –ï—Å–ª–∏ —Ñ–æ—Ç–æ–≥—Ä–∞—Ñ–∏–π –Ω–µ—Å–∫–æ–ª—å–∫–æ, –∑–∞–≥—Ä—É–∂–∞–π –∏—Ö –ø–æ –æ–¥–Ω–æ–π. –ü—Ä–æ–≥—Ä–∞–º–º–∞ –º–æ–∂–µ—Ç –º–µ–¥–ª–µ–Ω–Ω–æ —Ä–∞–±–æ—Ç–∞—Ç—å, –ø–æ–¥–æ–∂–¥–∏—Ç–µ!')


@bot.message_handler(commands=['global_warming'])
def send_warming(message):
    bot.send_message(message.chat.id, '–ì–ª–æ–±–∞–ª—å–Ω–æ–µ –ø–æ—Ç–µ–ø–ª–µ–Ω–∏–µ ‚Äî —ç—Ç–æ –ø–æ–≤—ã—à–µ–Ω–∏–µ —Å—Ä–µ–¥–Ω–µ–π —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã –Ω–∞ –ø–ª–∞–Ω–µ—Ç–µ –≤ —Ç–µ—á–µ–Ω–∏–µ –¥–ª–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø–µ—Ä–∏–æ–¥–∞.')
    bot.send_message(message.chat.id, '–ì–ª–æ–±–∞–ª—å–Ω–æ–µ –ø–æ—Ç–µ–ø–ª–µ–Ω–∏–µ –∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –º—É—Å–æ—Ä–∞ —Å–≤—è–∑–∞–Ω—ã —á–µ—Ä–µ–∑ —Å–Ω–∏–∂–µ–Ω–∏–µ –≤—ã–±—Ä–æ—Å–æ–≤ –ø–∞—Ä–Ω–∏–∫–æ–≤—ã—Ö –≥–∞–∑–æ–≤:')
    bot.send_message(message.chat.id, '–°–æ–∫—Ä–∞—â–µ–Ω–∏–µ —Å–≤–∞–ª–æ–∫. –û—Ä–≥–∞–Ω–∏—á–µ—Å–∫–∏–π –º—É—Å–æ—Ä –Ω–∞ —Å–≤–∞–ª–∫–∞—Ö —Ä–∞–∑–ª–∞–≥–∞–µ—Ç—Å—è –±–µ–∑ –∫–∏—Å–ª–æ—Ä–æ–¥–∞, –≤—ã–¥–µ–ª—è—è –º–µ—Ç–∞–Ω (–≤ 25 —Ä–∞–∑ –æ–ø–∞—Å–Ω–µ–µ CO‚ÇÇ –¥–ª—è –∫–ª–∏–º–∞—Ç–∞). –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ–∑–≤–æ–ª—è–µ—Ç –ø–µ—Ä–µ—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å –∏–ª–∏ –∫–æ–º–ø–æ—Å—Ç–∏—Ä–æ–≤–∞—Ç—å –æ—Ç—Ö–æ–¥—ã, —É–º–µ–Ω—å—à–∞—è –∏—Ö –æ–±—ä—ë–º.')
    bot.send_message(message.chat.id, '–≠–∫–æ–Ω–æ–º–∏—è —Ä–µ—Å—É—Ä—Å–æ–≤. –ü–µ—Ä–µ—Ä–∞–±–æ—Ç–∫–∞ (—Å—Ç–µ–∫–ª–∞, –ø–ª–∞—Å—Ç–∏–∫–∞, –º–µ—Ç–∞–ª–ª–∞) —Ç—Ä–µ–±—É–µ—Ç –º–µ–Ω—å—à–µ —ç–Ω–µ—Ä–≥–∏–∏, —á–µ–º –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–æ –Ω–æ–≤–æ–≥–æ —Å—ã—Ä—å—è ‚Üí —Å–Ω–∏–∂–∞—é—Ç—Å—è –≤—ã–±—Ä–æ—Å—ã CO‚ÇÇ –æ—Ç –∑–∞–≤–æ–¥–æ–≤.')
    bot.send_message(message.chat.id, '–ú–µ–Ω—å—à–µ –º—É—Å–æ—Ä–æ—Å–∂–∏–≥–∞–Ω–∏—è. –°–∂–∏–≥–∞–Ω–∏–µ –Ω–µ–ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∞–Ω–Ω–æ–≥–æ –º—É—Å–æ—Ä–∞ –≤—ã–±—Ä–∞—Å—ã–≤–∞–µ—Ç CO‚ÇÇ –∏ —Ç–æ–∫—Å–∏–Ω—ã. –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ —Å–æ–∫—Ä–∞—â–∞–µ—Ç –æ–±—ä—ë–º –æ—Ç—Ö–æ–¥–æ–≤ –¥–ª—è —Å–∂–∏–≥–∞–Ω–∏—è.')
    bot.send_message(message.chat.id, '–ò—Ç–æ–≥: –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –º—É—Å–æ—Ä–∞ ‚Äî –ø—Ä–æ—Å—Ç–æ–π —Å–ø–æ—Å–æ–± –∑–∞–º–µ–¥–ª–∏—Ç—å –ø–æ—Ç–µ–ø–ª–µ–Ω–∏–µ —á–µ—Ä–µ–∑ —Å–æ–∫—Ä–∞—â–µ–Ω–∏–µ –º–µ—Ç–∞–Ω–∞ –∏ CO‚ÇÇ. ‚ôªÔ∏èüåç')


@bot.message_handler(content_types=['photo'])
def send_class(message):
    file_info = bot.get_file(message.photo[-1].file_id)
    print(file_info)
    file_name = file_info.file_path.split('/')[-1]
    downloaded_file = bot.download_file(file_info.file_path)
    with open(file_name, 'wb') as new_file:
        new_file.write(downloaded_file)
    result = get_class(file_name)
    bot.send_message(message.chat.id, result)
    if result == '–ú–∞–∫—É–ª–∞—Ç—É—Ä–∞':
            bot.send_message(message.chat.id, '–ù–∞–ø–∏—à–∏ –∫–æ–º–∞–Ω–¥—É /paper, –∏ —Ç—ã —É–∑–Ω–∞–µ—à—å –ø–ª–∞–Ω —Å–¥–∞—á–∏ –±—É–º–∞–≥–∏ –Ω–∞ –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫—É')
    if result == '–ë–∞—Ç–∞—Ä–µ–π–∫–∏':
            bot.send_message(message.chat.id, '–ù–∞–ø–∏—à–∏ –∫–æ–º–∞–Ω–¥—É /batteries, –∏ —Ç—ã —É–∑–Ω–∞–µ—à—å –ø–ª–∞–Ω —Å–¥–∞—á–∏ –±–∞—Ç–∞—Ä–µ–µ–∫ –Ω–∞ –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫—É')
    if result == '–ü–ª–∞—Å—Ç–∏–∫':
                bot.send_message(message.chat.id, '–ù–∞–ø–∏—à–∏ –∫–æ–º–∞–Ω–¥—É /plastic, –∏ —Ç—ã —É–∑–Ω–∞–µ—à—å –ø–ª–∞–Ω —Å–¥–∞—á–∏ –ø–ª–∞—Å—Ç–∏–∫–∞ –Ω–∞ –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫—É')
    if result == '–°—Ç–µ–∫–ª–æ':
            bot.send_message(message.chat.id, '–ù–∞–ø–∏—à–∏ –∫–æ–º–∞–Ω–¥—É /glass, –∏ —Ç—ã —É–∑–Ω–∞–µ—à—å –ø–ª–∞–Ω —Å–¥–∞—á–∏ —Å—Ç–µ–∫–ª–∞ –Ω–∞ –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫—É')
    bot.send_message(message.chat.id, '–í —Å–ª—É—á–∞–µ, –µ—Å–ª–∏ –ø–µ—Ä–µ—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º—ã–π –º—É—Å–æ—Ä –±—ã–ª –æ–ø—Ä–µ–¥–µ–ª—ë–Ω –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ, –æ–ø–∏—à–∏—Ç–µ –≤–∞—à—É –ø—Ä–æ–±–ª–µ–º—É –≤ –ø–∏—Å—å–º–µ, –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–Ω–æ–º –Ω–∞ –ø–æ—á—Ç—É kumetsanton@gmail.com, –∏ –º—ã –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ —Ä–µ—à–∏–º –≤–∞—à—É –ø—Ä–æ–±–ª–µ–º—É.')


