@bot.message_handler(commands=['glass'])
def send_glass(message):
    bot.send_message(message.chat.id, '–û–ø—Ä–µ–¥–µ–ª–∏, –∫–∞–∫–æ–µ —Å—Ç–µ–∫–ª–æ –º–æ–∂–Ω–æ —Å–¥–∞—Ç—å. –ü–æ–¥–æ–π–¥—É—Ç –±—É—Ç—ã–ª–∫–∏ –æ—Ç –Ω–∞–ø–∏—Ç–∫–æ–≤, –±–∞–Ω–∫–∏ –∏–∑-–ø–æ–¥ –∫–æ–Ω—Å–µ—Ä–≤–æ–≤ –∏–ª–∏ –¥–µ—Ç—Å–∫–æ–≥–æ –ø–∏—Ç–∞–Ω–∏—è, –∞ —Ç–∞–∫–∂–µ —Ä–∞–∑–ª–∏—á–Ω—ã–µ –ø—É–∑—ã—Ä—å–∫–∏. –ú–æ–∂–Ω–æ —Å–¥–∞—Ç—å –ª–∏—Å—Ç–æ–≤–æ–µ —Å—Ç–µ–∫–ª–æ, –∫–æ—Ç–æ—Ä–æ–µ –æ—Å—Ç–∞–ª–æ—Å—å –ø—Ä–∏ –∑–∞–º–µ–Ω–µ –æ–∫–æ–Ω.')
    bot.send_message(message.chat.id, '–ü–æ–¥–≥–æ—Ç–æ–≤—å —Å—Ç–µ–∫–ª–æ –∫ —Å–¥–∞—á–µ. –ï–≥–æ –Ω—É–∂–Ω–æ –æ–ø–æ–ª–æ—Å–Ω—É—Ç—å –æ—Ç –æ—Å—Ç–∞—Ç–∫–æ–≤ —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ –∏ —Å–Ω—è—Ç—å —Å —Ç–∞—Ä—ã –∫—Ä—ã—à–∫—É. –≠—Ç–∏–∫–µ—Ç–∫—É –æ—Ç—Ä—ã–≤–∞—Ç—å –Ω–µ –Ω—É–∂–Ω–æ.')
    bot.send_message(message.chat.id, '–ù–∞–π–¥–∏ –ø—É–Ω–∫—Ç –ø—Ä–∏—ë–º–∞ —Å—Ç–µ–∫–ª–æ—Ç–∞—Ä—ã. –í —Ä–∞–∑–Ω—ã—Ö –≥–æ—Ä–æ–¥–∞—Ö –†–æ—Å—Å–∏–∏ –µ—Å—Ç—å —Ç–∞–∫–∏–µ –ø—É–Ω–∫—Ç—ã, –∞ —Ç–∞–∫–∂–µ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä—ã –¥–ª—è –≤—Ç–æ—Ä—Å—ã—Ä—å—è (–æ–±—ã—á–Ω–æ —Å–∏–Ω–µ–≥–æ —Ü–≤–µ—Ç–∞).')
    bot.send_message(message.chat.id, '–°–¥–∞–π —Å—Ç–µ–∫–ª–æ –≤ –ø—É–Ω–∫—Ç –ø—Ä–∏—ë–º–∞. –í–º–µ—Å—Ç–µ —Å–æ —Å—Ç–µ–∫–ª–æ–º –Ω–µ–ª—å–∑—è —Å–¥–∞–≤–∞—Ç—å –∫–µ—Ä–∞–º–∏—á–µ—Å–∫—É—é –∏ —Å—Ç–µ–∫–ª—è–Ω–Ω—É—é –ø–æ—Å—É–¥—É, –æ—á–∫–∏ –∏–ª–∏ –ª–∏–Ω–∑—ã, –∞–≤—Ç–æ–º–æ–±–∏–ª—å–Ω–æ–µ —Å—Ç–µ–∫–ª–æ, –∫—Ä—ã—à–∫–∏ –æ—Ç —Å–∫–æ–≤–æ—Ä–æ–¥–æ–∫ –∏ –∫–∞—Å—Ç—Ä—é–ª—å, –ª–∞–º–ø–æ—á–∫–∏, –∑–µ—Ä–∫–∞–ª–∞ –∏ —Ö—Ä—É—Å—Ç–∞–ª—å.')

@bot.message_handler(commands=['recycling'])
def send_recycling(message):
    markup = telebot.types.ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=True)
    items = ['–ë—É–º–∞–≥–∞', '–ë–∞—Ç–∞—Ä–µ–π–∫–∏', '–ü–ª–∞—Å—Ç–∏–∫', '–°—Ç–µ–∫–ª–æ']
    markup.add(*items)
    bot.send_message(message.chat.id, '–í—ã–±–µ—Ä–∏ —Ç–∏–ø –º—É—Å–æ—Ä–∞, —á—Ç–æ–±—ã —É–∑–Ω–∞—Ç—å –ø–ª–∞–Ω –µ–≥–æ –ø–µ—Ä–µ—Ä–∞–±–æ—Ç–∫–∏', reply_markup=markup)

# –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞
@bot.message_handler(func=lambda msg: msg.text in ['–ë—É–º–∞–≥–∞', '–ë–∞—Ç–∞—Ä–µ–π–∫–∏', '–ü–ª–∞—Å—Ç–∏–∫', '–°—Ç–µ–∫–ª–æ'])
def handle_recycling_choice(message):
    if message.text == '–ë—É–º–∞–≥–∞':
        send_paper(message)
    elif message.text == '–ë–∞—Ç–∞—Ä–µ–π–∫–∏':
        send_batteries(message)
    elif message.text == '–ü–ª–∞—Å—Ç–∏–∫':
        send_plastic(message)
    elif message.text == '–°—Ç–µ–∫–ª–æ':
        send_glass(message)

